text.info:
ConstantValue: precision mediump float;
uniform sampler2D tex_sampler_0;
uniform sampler2D tex_sampler_1;
uniform sampler2D tex_sampler_2;
uniform float weight;
varying vec2 v_texcoord;
void main() {
  vec4 colorL = texture2D(tex_sampler_0, v_texcoord);
  vec4 colorR = texture2D(tex_sampler_1, v_texcoord);
  float blend = texture2D(tex_sampler_2, v_texcoord).r * weight;
  gl_FragColor = colorL * (1.0 - blend) + colorR * blend;
}
