text.info:
ConstantValue: uniform sampler2D tex_sampler_0;
uniform sampler2D tex_sampler_1;
uniform sampler2D tex_sampler_2;
uniform float subsample_level;
varying vec2 v_texcoord;
void main() {
  vec4 fg_rgb = texture2D(tex_sampler_0, v_texcoord, subsample_level);
  vec4 fg = coeff_yuv * vec4(fg_rgb.rgb, 1.);
  vec4 mean = texture2D(tex_sampler_1, v_texcoord);
  vec4 variance = inv_var_scale * texture2D(tex_sampler_2, v_texcoord);

  float dist_y = gauss_dist_y(fg.r, mean.r, variance.r);
  float dist_uv = gauss_dist_uv(fg.gb, mean.gb, variance.gb);
  gl_FragColor = vec4(0.5*fg.rg, dist_scale*dist_y, dist_scale*dist_uv);
}

ConstantValue: uniform sampler2D tex_sampler_0;
uniform float accept_variance;
uniform vec2 yuv_weights;
uniform float scale_lrg;
uniform float scale_mid;
uniform float scale_sml;
uniform float exp_lrg;
uniform float exp_mid;
uniform float exp_sml;
varying vec2 v_texcoord;
bool is_fg(vec2 dist_yc, float accept_variance) {
  return ( dot(yuv_weights, dist_yc) >= accept_variance );
}
void main() {
  vec4 dist_lrg_sc = texture2D(tex_sampler_0, v_texcoord, exp_lrg);
  vec4 dist_mid_sc = texture2D(tex_sampler_0, v_texcoord, exp_mid);
  vec4 dist_sml_sc = texture2D(tex_sampler_0, v_texcoord, exp_sml);
  vec2 dist_lrg = inv_dist_scale * dist_lrg_sc.ba;
  vec2 dist_mid = inv_dist_scale * dist_mid_sc.ba;
  vec2 dist_sml = inv_dist_scale * dist_sml_sc.ba;
  vec2 norm_dist = 0.75 * dist_sml / accept_variance;
  bool is_fg_lrg = is_fg(dist_lrg, accept_variance * scale_lrg);
  bool is_fg_mid = is_fg_lrg || is_fg(dist_mid, accept_variance * scale_mid);
  float is_fg_sml =
      float(is_fg_mid || is_fg(dist_sml, accept_variance * scale_sml));
  float alpha = 0.5 * is_fg_sml + 0.3 * float(is_fg_mid) + 0.2 * float(is_fg_lrg);
  gl_FragColor = vec4(alpha, norm_dist, is_fg_sml);
}

ConstantValue: uniform sampler2D tex_sampler_0;
uniform sampler2D tex_sampler_1;
uniform float pyramid_depth;
uniform bool autowb_toggle;
varying vec2 v_texcoord;
void main() {
   vec4 mean_video = texture2D(tex_sampler_0, v_texcoord, pyramid_depth);
   vec4 mean_bg = texture2D(tex_sampler_1, v_texcoord, pyramid_depth);
   float green_normalizer = mean_video.g / mean_bg.g;
   vec4 adjusted_value = vec4(mean_bg.r / mean_video.r * green_normalizer, 1., 
                         mean_bg.b / mean_video.b * green_normalizer, 1.) * auto_wb_scale; 
   gl_FragColor = autowb_toggle ? adjusted_value : vec4(auto_wb_scale);
}

ConstantValue: uniform mat3 bg_fit_transform;
uniform float mask_blend_bg;
uniform float mask_blend_fg;
uniform float exposure_change;
uniform float whitebalancered_change;
uniform float whitebalanceblue_change;
uniform sampler2D tex_sampler_0;
uniform sampler2D tex_sampler_1;
uniform sampler2D tex_sampler_2;
uniform sampler2D tex_sampler_3;
varying vec2 v_texcoord;
void main() {
  vec2 bg_texcoord = (bg_fit_transform * vec3(v_texcoord, 1.)).xy;
  vec4 bg_rgb = texture2D(tex_sampler_1, bg_texcoord);
  vec4 wb_auto_scale = texture2D(tex_sampler_3, v_texcoord) * exposure_change / auto_wb_scale;
  vec4 wb_manual_scale = vec4(1. + whitebalancered_change, 1., 1. + whitebalanceblue_change, 1.);
  vec4 fg_rgb = texture2D(tex_sampler_0, v_texcoord);
  vec4 fg_adjusted = fg_rgb * wb_manual_scale * wb_auto_scale;
  vec4 mask = texture2D(tex_sampler_2, v_texcoord, 
                      2.0);
  float alpha = smoothstep(mask_blend_bg, mask_blend_fg, mask.a);
  gl_FragColor = mix(bg_rgb, fg_adjusted, alpha);

ConstantValue:   vec4 ghost_rgb = (fg_adjusted * 0.7 + vec4(0.3,0.3,0.4,0.))*0.65 + 
                   0.35*bg_rgb;
  float glow_start = 0.75 * mask_blend_bg; 
  float glow_max   = mask_blend_bg; 
  gl_FragColor = mask.a < glow_start ? bg_rgb : 
                 mask.a < glow_max ? mix(bg_rgb, vec4(0.9,0.9,1.0,1.0), 
                                     (mask.a - glow_start) / (glow_max - glow_start) ) : 
                 mask.a < mask_blend_fg ? mix(vec4(0.9,0.9,1.0,1.0), ghost_rgb, 
                                    (mask.a - glow_max) / (mask_blend_fg - glow_max) ) : 
                 ghost_rgb;
}

ConstantValue: uniform sampler2D tex_sampler_0;
uniform sampler2D tex_sampler_1;
uniform sampler2D tex_sampler_2;
uniform float subsample_level;
varying vec2 v_texcoord;
void main() {
  vec4 fg_rgb = texture2D(tex_sampler_0, v_texcoord, subsample_level);
  vec4 fg = coeff_yuv * vec4(fg_rgb.rgb, 1.);
  vec4 mean = texture2D(tex_sampler_1, v_texcoord);
  vec4 mask = texture2D(tex_sampler_2, v_texcoord, 
                      2.0);

  float alpha = local_adapt_rate(mask.a);
  vec4 new_mean = mix(mean, fg, alpha);
  gl_FragColor = new_mean;
}

ConstantValue: uniform sampler2D tex_sampler_0;
uniform sampler2D tex_sampler_1;
uniform sampler2D tex_sampler_2;
uniform sampler2D tex_sampler_3;
uniform float subsample_level;
varying vec2 v_texcoord;
void main() {
  vec4 fg_rgb = texture2D(tex_sampler_0, v_texcoord, subsample_level);
  vec4 fg = coeff_yuv * vec4(fg_rgb.rgb, 1.);
  vec4 mean = texture2D(tex_sampler_1, v_texcoord);
  vec4 variance = inv_var_scale * texture2D(tex_sampler_2, v_texcoord);
  vec4 mask = texture2D(tex_sampler_3, v_texcoord, 
                      2.0);

  float alpha = local_adapt_rate(mask.a);
  vec4 cur_variance = (fg-mean)*(fg-mean);
  vec4 new_variance = mix(variance, cur_variance, alpha);
  new_variance = max(new_variance, vec4(min_variance));
  gl_FragColor = var_scale * new_variance;
}

ConstantValue: uniform sampler2D tex_sampler_0;
uniform sampler2D tex_sampler_1;
uniform float verify_rate;
varying vec2 v_texcoord;
void main() {
  vec4 lastmask = texture2D(tex_sampler_0, v_texcoord);
  vec4 mask = texture2D(tex_sampler_1, v_texcoord);
  float newmask = mix(lastmask.a, mask.a, verify_rate);
  gl_FragColor = vec4(0., 0., 0., newmask);
}
