text.info:
ConstantValue: @import android.filterpacks.base;
@import android.filterpacks.videoproc;
@import android.filterpacks.videosrc;

@filter GLTextureSource foreground {
  texId = 0;
  width = 0;
  height = 0;
  repeatFrame = true;
}

@filter MediaSource background {
  sourceUrl = "no_file_specified";
  waitForNewFrame = false;
  sourceIsUrl = true;
}

@filter BackDropperFilter replacer {
  autowbToggle = 1;
}

@filter GLTextureTarget output {
  texId = 0;
}

@connect foreground[frame]  => replacer[video];
@connect background[video]  => replacer[background];
@connect replacer[video]    => output[frame];
